mapper.update:
UPDATE USER_PROFILE

        NICK_NAME = #{nickName}, 
        PROFILE_COMMENT = #{profileComment}, 
        PHOTO_NO = #{photoNo} 

WHERE USER_NO = #{userNo}

UPDATE USER_PROFILE
set PHOTO_NO = #{photoNo}
WHERE USER_NO = #{userNo}

mapper.select:
SELECT COUNT(*)
FROM USER_PROFILE
WHERE NICK_NAME = #{nickName}

mapper.update:
UPDATE USER_PROFILE
    SET NICK_NAME = #{nickName}
WHERE USER_NO = #{userNo}

UPDATE USER_PROFILE
    SET PROFILE_COMMENT = #{profileComment}
WHERE USER_NO = #{userNo}
