mapper.select:
SELECT T.* , (SELECT COUNT(*) FROM TEMPLATE T1 JOIN CHALLENGE C ON C.TEMPLATE_NO=T1.TEMPLATE_NO JOIN CHALLENGE_USER CU USING(CHALLENGE_NO) WHERE CU.STATUS='PLAY' AND T.TEMPLATE_NO=T1.TEMPLATE_NO)as PLAY_COUNT, --진행중인 사람수 (SELECT COUNT(*) FROM TEMPLATE T2 JOIN CHALLENGE C ON C.TEMPLATE_NO=T2.TEMPLATE_NO JOIN CHALLENGE_USER CU USING(CHALLENGE_NO) WHERE CU.STATUS='CLEAR' AND T.TEMPLATE_NO=T2.TEMPLATE_NO)as CLEAR_COUNT , --완료수 P.*,up.*,tc.* FROM TEMPLATE T LEFT JOIN USER_PROFILE up ON T.TP_CREATER = up.USER_NO LEFT JOIN PHOTO P ON UP.PHOTO_NO = P.PHOTO_NO JOIN TEMPLATE_CATEGORY tc on T.CATEGORY_NO=tc.CATEGORY_NO WHERE T.SAVE_STATUS=1
AND CATEGORY_NO=#{categoryNo}
ORDER BY T.CREATED_AT DESC

mapper.delete:
DELETE TEMPLATE
WHERE TP_CREATER = #{userNo}
  AND SAVE_STATUS = 0

mapper.update:
update template


        CATEGORY_NO=#{categoryNo},


        RANGE = #{range},


        TITLE = #{title},


        CONTENT= #{content}


where TEMPLATE_NO=#{templateNo}
AND TP_CREATER=#{userNo}

mapper.select:
SELECT COUNT(*)
FROM TEMPLATE
WHERE TP_CREATER = #{userNo}
  AND TEMPLATE_NO = #{refNo}
