mapper.update:
UPDATE TEMPLATE_TODO
SET CONTENT = #{content}
WHERE TP_TODO_NO = #{tpTodoNo}

UPDATE TEMPLATE_TODO
SET VALUE = VALUE - 1
WHERE TEMPLATE_NO = #{templateNo}
  AND VALUE > #{value}
  AND DAY = #{day}

mapper.delete:
DELETE TEMPLATE_TODO
WHERE TEMPLATE_NO = #{templateNo}
AND DAY>#{Range}

mapper.select:
SELECT COUNT(*)
FROM TEMPLATE_TODO
JOIN TEMPLATE USING(TEMPLATE_NO)
WHERE TP_CREATER=#{userNo}
  AND TP_TODO_NO=#{refNo}
